import BtnPrimary from "../buttons/BtnPrimary";
import BtnSecondary from "../buttons/BtnSecondary";

const Banner = () => {
    return ( 
        <div className="bg-banner bg-cover bg-no-repeat h-screen relative w-full overflow-hidden">
            <div className="gradient-overlay"></div>
            <div className="content-container text-white flex flex-col items-center text-center">
                <h1 className="text-md:text-6xl font-bold w-5/6 mb-20 mt-16">
                    <span className="text-orange-600">Enugu</span> Tech & Entrepreneurship Summit 2023
                </h1>
                <p className="text-lg font-semibold">
                    Great Innovative Ideas Unleashed
                </p>
                <div className="flex">
                    <BtnSecondary btnName={'Partner With Us'} />
                    <BtnPrimary btnName={'Register'} />
                </div>
            </div>
        </div>
    );
}
 
export default Banner;
